    private void bootomSheetDialog() {
        bottomSheetDialog = new BottomSheetDialog(MainActivity.this);
        layoutEpisdsosBottomShetBinding = DataBindingUtil.inflate(
                LayoutInflater.from(MainActivity.this)
                , R.layout.layout_address_bottom_shet, findViewById(R.id.espiodesCountener), false
        );
        bottomSheetDialog.setContentView(layoutEpisdsosBottomShetBinding.getRoot());

        layoutEpisdsosBottomShetBinding.layoutSelctonmap.setOnClickListener(v -> startActivity(new Intent(MainActivity.this, MapsActivity.class)));
        //-----optional section-------//
        FrameLayout frameLayout = bottomSheetDialog.findViewById(
                com.google.android.material.R.id.design_bottom_sheet
        );
        if (frameLayout != null) {
            BottomSheetBehavior<View> bottomSheetBehavior = BottomSheetBehavior.from(frameLayout);
            bottomSheetBehavior.setPeekHeight(Resources.getSystem().getDisplayMetrics().heightPixels);
            bottomSheetBehavior.setState(BottomSheetBehavior.STATE_EXPANDED);
        }
        //-----optional section-------//

        bottomSheetDialog.show();
    }

