    private void initSliderAdapter2(final List<MarketModel> silderList, ViewPager viewPager) {
        sliderAdapter2 = new GenericSliderAdapter<MarketModel>(this, silderList) {
            @Override
            public View getMyView(ViewGroup container, int position, final MarketModel data) {

                LayoutInflater layoutInflater = (LayoutInflater)
                        getSystemService(Context.LAYOUT_INFLATER_SERVICE);
                final View item_view = layoutInflater.inflate(R.layout.item_slider, container, false);
                ImageView imgSlide = item_view.findViewById(R.id.image_slider);

                TextView tv_name = item_view.findViewById(R.id.tv_name_);
                TextView tv_order = item_view.findViewById(R.id.tv_name_order);
                TextView tv_desc = item_view.findViewById(R.id.tv_name_order_desc);
                TextView tv_rate = item_view.findViewById(R.id.tv_rate_order);


                Glide.with(MainActivity.this)
                        .load(silderList.get(position).getImage())
                        .placeholder(R.drawable.logo)
                        .into(imgSlide);
                tv_name.setText(data.getNameMarket());
                tv_desc.setText(data.getDescMarket());
                tv_order.setText(data.getPriceOrder());
                tv_rate.setText(data.getRate());

                item_view.setOnClickListener(view -> Toast.makeText(MainActivity.this, "ok", Toast.LENGTH_SHORT).show());


                container.addView(item_view);
                return item_view;
            }
        };
        viewPager.setAdapter(sliderAdapter2);
    }

